import React, { useState } from "react";
import "./Project.css";

function Project({ project }) {
  const [bcgImg, setBcgImg] = useState(project.imageUrl);

  return (
    <>
      <div className="project" onMouseEnter={() => setBcgImg(project.videoUrl)}>
        <div style={{ backgroundImage: `url(${bcgImg})` }}></div>
        <div className="project-banner"></div>
      </div>
    </>
  );
}

export default Project;
