import React, { useState } from "react";
import "./Project.css";

function Project({ project }) {
  const [remountCount, setRemountCount] = useState(0);
  const refresh = () => setRemountCount(remountCount + 1);

  let bcgImg = project.imageUrl;

  const setBcgImg = () => {
    console.log("IT runs");
    bcgImg = project.videoUrl;
    console.log("bchImg: ", bcgImg);
    refresh();
  };

  return (
    <div
      className="project"
      style={{ backgroundImage: `url(${bcgImg})` }}
      onMouseEnter={() => setBcgImg()}
    >
      <div className="project-banner"></div>
    </div>
  );
}

export default Project;
